CREATE TABLE IF NOT EXISTS `users` ( `i_id` INT NOT NULL AUTO_INCREMENT , `v_firstName` VARCHAR(25) NOT NULL , `v_lastName` VARCHAR(25) NOT NULL , `v_email` VARCHAR(50) NOT NULL , PRIMARY KEY (`i_id`)) ENGINE = InnoDB;
CREATE TABLE IF NOT EXISTS `account` ( `i_id` INT NOT NULL AUTO_INCREMENT , `i_balance` INT(25) NOT NULL , `i_idUser` INT(10) NOT NULL , PRIMARY KEY (`i_id`)) ENGINE = InnoDB;
ALTER TABLE `account` ADD CONSTRAINT `fk_u_idUser` FOREIGN KEY IF NOT EXISTS (`i_idUser`) REFERENCES `users`(`i_id`) ON DELETE RESTRICT ON UPDATE RESTRICT;